para.programlisting:
#!/usr/bin/env jruby
require 'rubygems' require 'org.torquebox.torquebox-messaging-client'
TorqueBox::Messaging::Client.connect do |session| queue = session.createQueue( '/queues/foo' ) producer = session.createProducer( queue ) message = session.createTextMessage( "The time is #{Time.now}" ) producer.send( message ) session.commit end
